mapper.update:

update board
   set o_no=o_no+1 
 where g_no = #{groupNo }
   and o_no >= #{orderNo };

mapper.select:

update board
   set title = #{title },
       contents = #{contents }
 where no = #{no }

mapper.update:

update board b inner join user u on u.no = b.user_no
   set b.status = 'd', b.title = '삭제된 글입니다.'
 where b.no = #{no }
   and u.no = #{userNo };
